it("reports runtime metrics", function (done) {
  var stopFlows = sinon.stub(redNodes, "stopFlows").callsFake(function () {
    return Promise.resolve();
  });
  redNodesGetNodeList = sinon.stub(redNodes, "getNodeList").callsFake(function () {
    return [];
  });
  unmockUtil();
  mockUtil(true);
  runtime.init({
    testSettings: true,
    runtimeMetricInterval: 200,
    httpAdminRoot: "/",
    load: function () {
      return Promise.resolve();
    }
  }, {}, undefined);
  // sinon.stub(console,"log");
  runtime.start().then(function () {
    // console.log.restore();
    setTimeout(function () {
      try {
        log.log.args.should.have.lengthOf(3);
        log.log.args[0][0].should.have.property("event", "runtime.memory.rss");
        log.log.args[1][0].should.have.property("event", "runtime.memory.heapTotal");
        log.log.args[2][0].should.have.property("event", "runtime.memory.heapUsed");
        done();
      } catch (err) {
        done(err);
      } finally {
        runtime.stop();
        stopFlows.restore();
      }
    }, 300);
  }).catch(err => {
    done(err);
  });
});

it("stops components", function (done) {
  var stopFlows = sinon.stub(redNodes, "stopFlows").callsFake(function () {
