it('should transform incoming bytes to base64', function (done) {
  let encoder = new base64.Encoder({
    lineLength: 9
  });
  let bytes = Buffer.from(streamFixture[0]),
    i = 0,
    buf = [],
    buflen = 0;
  encoder.on('data', function (chunk) {
    buf.push(chunk);
    buflen += chunk.length;
  });
  encoder.on('end', function (chunk) {
    if (chunk) {
      buf.push(chunk);
      buflen += chunk.length;
    }
    buf = Buffer.concat(buf, buflen);
    expect(buf.toString()).to.equal(streamFixture[1]);
    done();
  });
  let sendNextByte = function () {
    if (i >= bytes.length) {
      return encoder.end();
    }
    let ord = bytes[i++];
    encoder.write(Buffer.from([ord]));
    setImmediate(sendNextByte);
  };
  sendNextByte();
});

it('should transform incoming bytes to base64 and back', function (done) {
  let decoder = new libbase64.Decoder();
  let encoder = new base64.Encoder();
  let file = fs.createReadStream(__dirname + '/fixtures/alice.txt');
  let fhash = crypto.createHash('md5');
  let dhash = crypto.createHash('md5');
  file.pipe(encoder).pipe(decoder);
  file.on('data', function (chunk) {
    fhash.update(chunk);
  });
  file.on('end', function () {
    fhash = fhash.digest('hex');
  });
  decoder.on('data', function (chunk) {
    dhash.update(chunk);
  });
  decoder.on('end', function () {
    dhash = dhash.digest('hex');
    expect(fhash).to.equal(dhash);
    done();
  });
});

  it('should sign message', function (done) {
    let message = `From: saatja aadress
To: Saaja aadress
Subject: pealkiri
  mitmel
  real
Message-Id: test

tere tere
teine rida
`;
    let s = new PassThrough();
    let dkim = new DKIM({
      domainName: 'node.ee',
      keySelector: 'dkim',
      privateKey
    });
    let output = dkim.sign(s);
    let chunks = [];
    let reading = false;
    let readNext = () => {
      let chunk = output.read(10 * 1024);
      if (chunk === null) {
        reading = false;
        return;
      }
      reading = true;
      chunks.push(chunk);
      setImmediate(readNext);
    };
    output.on('readable', () => {
      if (!reading) {
        readNext();
      }
    });
    output.on('end', () => {
      let message = Buffer.concat(chunks).toString();
      expect(message).to.equal('DKIM-Signature: v=1; a=rsa-sha256; c=relaxed/relaxed; d=node.ee; q=dns/txt;\r\n' + ' s=dkim; bh=h2JdEKA6yVYSGuI3DQCDlg2KL+96GxA7Yw7owvsYDUM=;\r\n' + ' h=from:subject:message-id:to;\r\n' + ' b=tey8mL2VQVuy/phh7yEKi86Y0Yyzyy04qTy73G4yg3qdEDB7uymjNr32ruRPVFmC9PimIK75p\r\n' + ' KVPF5T1oB8/oY6TFqyyuECRrI4D2CfG3VXWBShK32r1Wtv7eWN04b4s\r\n' + 'From: saatja aadress\n' + 'To: Saaja aadress\n' + 'Subject: pealkiri\n' + '  mitmel\n' + '  real\n' + 'Message-Id: test\n' + '\n' + 'tere tere\n' + 'teine rida\n');
      done();
    });
    let inputPos = 0;
    let messageBuf = Buffer.from(message);
    let writeNext = () => {
      if (inputPos >= messageBuf.length) {
        return s.end();
      }
      s.write(Buffer.from([messageBuf[inputPos++]]));
      setImmediate(writeNext);
    };
    writeNext();
  });

it('should sign large message using cache dir', function (done) {
  let dkim = new DKIM({
    domainName: 'node.ee',
    keySelector: 'dkim',
    privateKey,
    cacheDir: path.join(__dirname, 'cache')
  });
  let output = dkim.sign(fs.createReadStream(__dirname + '/fixtures/large.eml'));
  output.on('error', err => {
    expect(err).to.not.exist;
    done();
  });
  let chunks = [];
  let reading = false;
  let readNext = () => {
    let chunk = output.read();
    if (chunk === null) {
      reading = false;
      return;
    }
    reading = true;
    chunks.push(chunk);
    setImmediate(readNext);
  };
  output.on('readable', () => {
    if (!reading) {
      readNext();
    }
  });
  output.on('end', () => {
    let message = Buffer.concat(chunks).toString();
    expect(message.indexOf('bh=ST+2Z7mCDd8CPa6pWbCWnFBLKyl8/I5d0JCaEQub550=')).is.gte(0);
    expect(crypto.createHash('md5').update(Buffer.from(message.substr(message.indexOf('X-Zone-Loop')))).digest('hex')).to.equal('16078d67ecb4c9954f2568b3bd20e8b5');
    expect(output.usingCache).to.be.true;
    done();
  });
});

