  it('should extract header and body', function (done) {
    let parser = new MessageParser();
    let message = `From: saatja aadress
To: Saaja aadress
Subject: pealkiri
  mitmel
  real
Message-Id: test

tere tere
teine rida
`;
    let chunks = [];
    let headers = false;
    let end = false;
    parser.on('data', chunk => {
      chunks.push(chunk);
    });
    parser.on('end', () => {
      end = true;
      let body = Buffer.concat(chunks).toString();
      expect(body).to.equal('tere tere\nteine rida\n');
      if (headers) {
        return done();
      }
    });
    parser.on('headers', data => {
      expect(data).to.deep.equal([
      // fix auto format
      {
        key: 'from',
        line: 'From: saatja aadress'
      }, {
        key: 'to',
        line: 'To: Saaja aadress'
      }, {
        key: 'subject',
        line: 'Subject: pealkiri\n  mitmel\n  real'
      }, {
        key: 'message-id',
        line: 'Message-Id: test'
      }]);
      headers = true;
      if (end) {
        return done();
      }
    });
    parser.end(Buffer.from(message));
  });

it('Should calculate body hash byte by byte', function (done) {
  fs.readFile(__dirname + '/fixtures/message1.eml', 'utf-8', (err, message) => {
    expect(err).to.not.exist;
    message = message.replace(/\r?\n/g, '\r\n');
    message = message.split('\r\n\r\n');
    message.shift();
    message = message.join('\r\n\r\n');
    message = Buffer.from(message);
    let s = new RelaxedBody({
      hashAlgo: 'sha256',
      debug: true
    });
    s.on('hash', hash => {
      expect(hash).to.equal('D2H5TEwtUgM2u8Ew0gG6vnt/Na6L+Zep7apmSmfy8IQ=');
      done();
    });
    let pos = 0;
    let stream = () => {
      if (pos >= message.length) {
        return s.end();
      }
      let ord = Buffer.from([message[pos++]]);
      s.write(ord);
      setImmediate(stream);
    };
    setImmediate(stream);
  });
});

