it('should quote filename with special characters', function () {
  // The case of browser downloads when we download multiple files with same name.
  expect(mimeFuncs.buildHeaderValue({
    value: 'test',
    params: {
      filename: 'receipt(3).pdf'
    }
  })).to.equal('test; filename="receipt(3).pdf"');

  // For headers which are comma separated as in case of multiple from members elements.
  expect(mimeFuncs.buildHeaderValue({
    value: 'test',
    params: {
      filename: 'jack,jill.pdf'
    }
  })).to.equal('test; filename="jack,jill.pdf"');

  // Added support for some more special characters
  let correctString = ['space="x y"', 'small_bracket_open="x(y"', 'small_bracket_close="x)y"', 'angle_bracket_open="x<y"', 'angle_bracket_close="x>y"', 'at_the_rate="x@y"', 'semicolon="x;y"', 'colon="x:y"', 'back_slash="x\\\\y"', 'single_quote="x\'y"', "double_quotes*0*=utf-8''x%22y", 'forward_slash="x/y"', 'big_bracket_open="x[y"', 'big_bracket_close="x]y"', 'question_mark="x?y"', 'comma="x,y"', 'equals="x=y"', 'negative_in_mid=x-y', 'negative_in_start="-x"'].join('; ');
  expect(mimeFuncs.buildHeaderValue({
    value: 'test',
    params: {
      space: 'x y',
      small_bracket_open: 'x(y',
      small_bracket_close: 'x)y',
      angle_bracket_open: 'x<y',
      angle_bracket_close: 'x>y',
      at_the_rate: 'x@y',
      semicolon: 'x;y',
      colon: 'x:y',
      back_slash: 'x\\y',
      single_quote: 'x\x27y',
      double_quotes: 'x"y',
      forward_slash: 'x/y',
      big_bracket_open: 'x[y',
      big_bracket_close: 'x]y',
      question_mark: 'x?y',
      comma: 'x,y',
      equals: 'x=y',
      negative_in_mid: 'x-y',
      negative_in_start: '-x'
    }
  })).to.equal('test; ' + correctString);
});

