it('should pipe through provided stream', function (done) {
  let mb = new MimeNode('text/plain').setHeader({
    date: '12345',
    'message-id': '67890'
  }).setContent('Hello world!');
  let expected = 'Content-Type:\ttext/plain\r\n' + 'Date:\t12345\r\n' + 'Message-ID:\t<67890>\r\n' + 'Content-Transfer-Encoding:\t7bit\r\n' + 'MIME-Version:\t1.0\r\n' + '\r\n' + 'Hello\tworld!\r\n';

  // Transform stream that replaces all spaces with tabs
  let transform = new Transform();
  transform._transform = function (chunk, encoding, done) {
    if (encoding !== 'buffer') {
      chunk = Buffer.from(chunk, encoding);
    }
    for (let i = 0, len = chunk.length; i < len; i++) {
      if (chunk[i] === 0x20) {
        chunk[i] = 0x09;
      }
    }
    this.push(chunk);
    done();
  };
  mb.transform(transform);
  mb.build(function (err, msg) {
    expect(err).to.not.exist;
    msg = msg.toString();
    expect(msg).to.equal(expected);
    done();
  });
});

it('should pipe through provided process function', function (done) {
  let mb = new MimeNode('text/plain').setHeader({
    date: '12345',
    'message-id': '67890'
  }).setContent('Hello world!');
  let expected = 'Content-Type:\ttext/plain\r\n' + 'Date:\t12345\r\n' + 'Message-ID:\t<67890>\r\n' + 'Content-Transfer-Encoding:\t7bit\r\n' + 'MIME-Version:\t1.0\r\n' + '\r\n' + 'Hello\tworld!\r\n';

  // Transform stream that replaces all spaces with tabs
  let transform = new Transform();
  transform._transform = function (chunk, encoding, done) {
    if (encoding !== 'buffer') {
      chunk = Buffer.from(chunk, encoding);
    }
    for (let i = 0, len = chunk.length; i < len; i++) {
      if (chunk[i] === 0x20) {
        chunk[i] = 0x09;
      }
    }
    this.push(chunk);
    done();
  };
  mb.processFunc(input => {
    setImmediate(() => input.pipe(transform));
    return transform;
  });
  mb.build(function (err, msg) {
    expect(err).to.not.exist;
    msg = msg.toString();
    expect(msg).to.equal(expected);
    done();
  });
});

