    logger: false
  });
  let message = 'teretere, vana kere\n';
  let startTime = Date.now();
  server.onData = function (stream, session, callback) {
    let chunks = [];
    stream.on('data', function (chunk) {
      chunks.push(chunk);
    });
    stream.on('end', function () {
      let body = Buffer.concat(chunks);
