it('throws an error on invalid colors', function () {
  const invalidColors = ['tuesday', '#12345', '#1234567'];
  let i, ii;
  for (i = 0, ii = invalidColors.length; i < ii; ++i) {
    expect(function () {
      fromString(invalidColors[i]);
    }).to.throwException();
  }
});

