it('can read, transform and write a point geometry', function () {
  const config = {
    featureProjection: 'EPSG:3857'
  };
  const text = '<gml:Point xmlns:gml="http://www.opengis.net/gml/3.2" ' + '    srsName="CRS:84">' + '  <gml:pos>1 2</gml:pos>' + '</gml:Point>';
