it('can transform and write XYZ Point geometries', function () {
  addProjection(new Projection({
    code: 'double'
  }));
  addCoordinateTransforms('EPSG:4326', 'double', function (coordinate) {
    return [2 * coordinate[0], 2 * coordinate[1]];
  }, function (coordinate) {
    return [coordinate[0] / 2, coordinate[1] / 2];
  });
  const layout = 'XYZ';
  const point = new Point([1, 2, 3], layout).transform('EPSG:4326', 'double');
  const features = [new Feature(point)];
  const node = format.writeFeaturesNode(features, {
    featureProjection: 'double'
  });
  const text = '<kml xmlns="http://www.opengis.net/kml/2.2"' + ' xmlns:gx="http://www.google.com/kml/ext/2.2"' + ' xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance"' + ' xsi:schemaLocation="http://www.opengis.net/kml/2.2' + ' https://developers.google.com/kml/schema/kml22gx.xsd">' + '  <Placemark>' + '    <Point>' + '      <coordinates>1,2,3</coordinates>' + '    </Point>' + '  </Placemark>' + '</kml>';
  expect(node).to.xmleql(parse(text));
  removeTransform(getProjection('EPSG:4326'), getProjection('double'));
  removeTransform(getProjection('double'), getProjection('EPSG:4326'));
});

