it("can write an feature's icon style", function () {
  const style = new Style({
    image: new Icon({
      anchor: [0.25, 36],
      anchorOrigin: 'top-left',
      anchorXUnits: 'fraction',
      anchorYUnits: 'pixels',
      crossOrigin: 'anonymous',
      offset: [96, 96],
      offsetOrigin: 'top-left',
