  // 0.5 * 48 / 32
  '        <heading>45</heading>' + '        <Icon>' + '          <href>http://foo.png</href>' + '          <gx:x>96</gx:x>' + '          <gx:y>0</gx:y>' + '          <gx:w>48</gx:w>' + '          <gx:h>48</gx:h>' + '        </Icon>' + '        <color>ff0000ff</color>' + '        <hotSpot x="12" y="12" xunits="pixels" ' + '                 yunits="pixels"/>' + '      </IconStyle>' + '      <PolyStyle>' + '        <fill>0</fill>' + '        <outline>0</outline>' + '      </PolyStyle>' + '    </Style>' + '  </Placemark>' + '</kml>';
