it('should read OperationsMetadata tag properly', function () {
  const doc = parse('<ows:GetCapabilities xmlns:ows="http://www.opengis.net/ows/1.1" ' + 'xmlns:xlink="http://www.w3.org/1999/xlink" >' + '<ows:OperationsMetadata>' + '<ows:Operation name="GetCapabilities">' + '<ows:DCP>' + '<ows:HTTP>' + '<ows:Get xlink:href=' + '"http://www.miramon.uab.es/cgi-bin/MiraMon5_0.cgi?">' + '<ows:Constraint name="GetEncoding">' + '<ows:AllowedValues>' + '<ows:Value>KVP</ows:Value>' + '<ows:Value>SOAP</ows:Value>' + '</ows:AllowedValues>' + '</ows:Constraint>' + '</ows:Get>' + '</ows:HTTP>' + '</ows:DCP>' + '</ows:Operation>' + '<ows:Operation name="GetTile">' + '<ows:DCP>' + '<ows:HTTP>' + '<ows:Get xlink:href="http://www.miramon.uab.es/cgi-' + 'bin/MiraMon5_0.cgi?"/>' + '<ows:Get xlink:href="http://www.miramon.uab.es/cgi-' + 'bin/MiraMon6_0.cgi?"/>' + '<ows:Post xlink:href="http://www.miramon.uab.es/cgi-' + 'bin/MiraMon7_0.cgi?"/>' + '</ows:HTTP>' + '</ows:DCP>' + '</ows:Operation>' + '<ows:Operation name="GetFeatureInfo">' + '<ows:DCP>' + '<ows:HTTP>' + '<ows:Get xlink:href="http://www.miramon.uab.es/cgi-' + 'bin/MiraMon5_0.cgi?"/>' + '</ows:HTTP>' + '</ows:DCP>' + '</ows:Operation>' + '</ows:OperationsMetadata>' + '</ows:GetCapabilities>');
  const obj = parser.readFromNode(doc.firstChild);
  expect(obj).to.be.ok();
  const operationsMetadata = obj.OperationsMetadata;
  expect(operationsMetadata).to.be.ok();
  const getCap = operationsMetadata.GetCapabilities;
  let dcp = getCap.DCP;
  let url = 'http://www.miramon.uab.es/cgi-bin/MiraMon5_0.cgi?';
  expect(dcp.HTTP.Get[0].href).to.eql(url);
  expect(dcp.HTTP.Get[0].Constraint[0].name).to.eql('GetEncoding');
  expect(dcp.HTTP.Get[0].Constraint[0].AllowedValues.Value[0]).to.eql('KVP');
  url = 'http://www.miramon.uab.es/cgi-bin/MiraMon5_0.cgi?';
  dcp = operationsMetadata.GetFeatureInfo.DCP;
  expect(dcp.HTTP.Get[0].href).to.eql(url);
  expect(dcp.HTTP.Get[0].Constraint).to.be(undefined);
  url = 'http://www.miramon.uab.es/cgi-bin/MiraMon5_0.cgi?';
  dcp = operationsMetadata.GetTile.DCP;
  expect(dcp.HTTP.Get[0].href).to.eql(url);
  expect(dcp.HTTP.Get[0].Constraint).to.be(undefined);
});

