    expect(receivedEvents.addfeature).to.be(0);
    ++receivedEvents.end;
  });
  source.on('addfeature', function () {
    expect(receivedEvents.end).to.be(1);
    expect(receivedEvents.addfeature).to.be(0);
    receivedEvents.addfeature++;
  });
