  simulateEvent('pointerdown', 10, 20);
  simulateEvent('pointerup', 10, 20);
  simulateEvent('pointermove', 20, 20);
  expect(receivedEvents.end).to.be(1);
  expect(receivedEvents.addfeature).to.be(1);
});

