it('all geometries should be modified', function () {
  const firstPolygon = new Polygon([[[0, 0], [1, 0], [1, 1], [0, 1], [0, 0]]]);
  const secondPolygon = firstPolygon.clone();
  const firstLineString = new LineString([[-2, 0], [0, 0], [2, 0]]);
  const secondLineString = new LineString([[0, 2], [0, 0], [0, -2]]);
  const point = new Point([0, 0]);
