it('hit detects two distinct features', function (done) {
  const feature = new Feature({
    geometry: new Point([0, 0]),
    id: 1,
    weight: 10
  });
  const feature2 = new Feature({
    geometry: new Point([14, 14]),
    id: 2,
    weight: 10
  });
  const source = new VectorSource({
    features: [feature, feature2]
  });
  layer = new HeatmapLayer({
    source: source,
    blur: 10,
    radius: 10
  });
  map.addLayer(layer);
  map.render();
  function hitTest(coordinate) {
    const features = map.getFeaturesAtPixel(map.getPixelFromCoordinate(coordinate));
    return features.length ? features[0] : null;
  }
  const renderer = layer.getRenderer();
  renderer.worker_.addEventListener('message', function (event) {
    if (!renderer.renderInstructions_) {
      return;
    }
    map.renderSync();
    let res;
    res = hitTest([0, 0]);
    expect(res).to.be(feature);
    res = hitTest([20, 0]);
    expect(res).to.be(null);
    res = hitTest([14, 14]);
    expect(res).to.be(feature2);
    res = hitTest([0, 14]);
    expect(res).to.be(null);
    done();
  });
});

