it('finds correct geometry when overlapping', function () {
  const bottomPoint = new Feature(new Point([90, 100]));
  const bottomLineString = new Feature(new LineString([[0, 140], [200, 140]]));
  const bottomPolygon = new Feature(new Polygon([[[0, 0], [0, 200], [200, 200], [200, 0], [0, 0]]]));
  const geometryCollection = new Feature(new GeometryCollection([new Point([100, 100]), new LineString([[0, 160], [200, 160]]), new Polygon([[[66, 0], [66, 200], [200, 200], [200, 0], [66, 0]]])]));
  const topPoint = new Feature(new Point([110, 100]));
  const topLineString = new Feature(new LineString([[0, 180], [200, 180]]));
  const topPolygon = new Feature(new Polygon([[[133, 0], [133, 200], [200, 200], [200, 0], [133, 0]]]));
  features = [bottomPoint, bottomLineString, bottomPolygon, geometryCollection, topPoint, topLineString, topPolygon];
  styleFunction = function (feature) {
    const index = features.indexOf(feature);
    const color = [index & 4 ? 200 : 0, index & 2 ? 200 : 0, index & 1 ? 200 : 0];
    const geometry = feature.getGeometry();
    const geometries = geometry instanceof GeometryCollection ? geometry.getGeometriesArrayRecursive() : [geometry];
    return geometries.map(function (geometry) {
      const type = geometry.getType();
      return new Style({
        geometry: geometry,
        fill: new Fill({
          color: color
        }),
        stroke: type === 'LineString' ? new Stroke({
          color: color,
          width: 150
        }) : undefined,
        image: new Circle({
          radius: 40,
          fill: new Fill({
            color: color
          })
        })
      });
    });
  };
  const imageData = createHitDetectionImageData([200, 200], [[0.5, 0, 0, -0.5, 0, 100]], features, styleFunction, [0, 0, 200, 200], 1, 0);
  expect(hitDetect([55, 100], features, imageData)[0]).to.be(bottomPoint);
  expect(hitDetect([65, 100], features, imageData)[0]).to.be(geometryCollection);
  expect(hitDetect([75, 100], features, imageData)[0]).to.be(topPoint);
  expect(hitDetect([33, 190], features, imageData)[0]).to.be(bottomPolygon);
  expect(hitDetect([100, 190], features, imageData)[0]).to.be(geometryCollection);
  expect(hitDetect([166, 190], features, imageData)[0]).to.be(topPolygon);
  expect(hitDetect([10, 130], features, imageData)[0]).to.be(bottomLineString);
  expect(hitDetect([10, 110], features, imageData)[0]).to.be(geometryCollection);
  expect(hitDetect([10, 90], features, imageData)[0]).to.be(topLineString);
});

