it('creates the correct canvas instructions for 3D geometries', function () {
  const instructions = [];
  function serialize(index, instruction) {
    if (!instruction) {
      return 'id: ' + index + ' NO INSTRUCTION';
    }
    const parts = ['id: ' + index, 'type: ' + instruction.type];
    if (instruction.args) {
      parts.push('args: [' + instruction.args.map(function (arg) {
        if (typeof arg === 'number') {
          return arg.toFixed(9);
        }
        return arg;
      }).join(', ') + ']');
    }
    return parts.join(', ');
  }
  const context = {
    beginPath: function () {},
    moveTo: function (x, y) {
      instructions.push({
        type: 'moveTo',
        args: [x, y]
      });
    },
    lineTo: function (x, y) {
      instructions.push({
        type: 'lineTo',
        args: [x, y]
      });
    },
    closePath: function () {
      instructions.push({
        type: 'closePath'
      });
    },
    setLineDash: function () {},
    stroke: function () {}
  };
  const transform = [0.0004088332670837288, 0, 0, -0.0004088332670837288, 4480.991370439071, 1529.5752568707105];
  const extent = [-10960437.252092224, 2762924.0275091752, -7572748.158493212, 3741317.9895594316];
  const canvas = new CanvasImmediateRenderer(context, 1, extent, transform);
  canvas.strokeState_ = {
    lineCap: 'round',
    lineDash: [],
    lineJoin: 'round',
    lineWidth: 3,
    miterLimit: 10,
    strokeStyle: '#00FFFF'
  };
  const multiPolygonGeometry = new MultiPolygon([[[
  // first polygon
  [-80.736061, 28.788576000000006, 0],
  // moveTo()
  [-80.763557, 28.821799999999996, 0],
  // lineTo()
  [-80.817406, 28.895123999999996, 0],
  // lineTo()
  [-80.891304, 29.013130000000004, 0],
  // lineTo()
  [-80.916512, 29.071560000000005, 0],
  // lineTo()
  [-80.899323, 29.061249000000004, 0],
  // lineTo()
  [-80.862663, 28.991361999999995, 0],
  // lineTo()
  [-80.736061, 28.788576000000006, 0] //  closePath()
  ]], [[
  // second polygon
  [-82.102127, 26.585724, 0],
  //          moveTo()
  [-82.067139, 26.497208, 0],
  //          lineTo()
  [-82.097641, 26.493585999999993, 0],
  // lineTo()
  [-82.135895, 26.642279000000002, 0],
  // lineTo()
  [-82.183495, 26.683082999999996, 0],
  // lineTo()
  [-82.128838, 26.693342, 0],
  //          lineTo()
  [-82.102127, 26.585724, 0] //           closePath()
  ]]]).transform('EPSG:4326', 'EPSG:3857');
  canvas.drawMultiPolygon(multiPolygonGeometry);
  const expected = [
  // first polygon
  {
    type: 'moveTo',
    args: [806.6035275946265, 160.48916296287916]
  }, {
    type: 'lineTo',
    args: [805.3521540835154, 158.76358389011807]
  }, {
    type: 'lineTo',
    args: [802.9014262612932, 154.95335187132082]
  }, {
    type: 'lineTo',
    args: [799.5382461724039, 148.815592819916]
  }, {
    type: 'lineTo',
    args: [798.3910020835165, 145.77392230456553]
  }, {
    type: 'lineTo',
    args: [799.1732925724045, 146.31080369865776]
  }, {
    type: 'lineTo',
    args: [800.8417299057378, 149.94832216046188]
  }, {
    type: 'closePath'
  },
  // second polygon
  {
    type: 'moveTo',
    args: [744.4323460835158, 273.7179168205373]
  }, {
    type: 'lineTo',
    args: [746.0246888390716, 278.22094795365365]
  }, {
    type: 'lineTo',
    args: [744.6365089279602, 278.40513424671826]
  }, {
    type: 'lineTo',
    args: [742.8955268835157, 270.83899948444764]
  }, {
    type: 'lineTo',
    args: [740.7291979946272, 268.76099731369345]
  }, {
    type: 'lineTo',
    args: [743.2166987946266, 268.23842607400616]
  }, {
    type: 'closePath'
  }];
  for (let i = 0, ii = instructions.length; i < ii; ++i) {
    const actualInstruction = serialize(i, instructions[i]);
    const expectedInstruction = serialize(i, expected[i]);
    expect(actualInstruction).to.equal(expectedInstruction);
  }
  expect(instructions.length).to.equal(expected.length);
});

it('does not trigger redraw and clear measurements for unavailable fonts', function (done) {
