  res = hitTest([0, 1.5]);
  expect(res.count).to.be(1);
  expect(res.extent[0]).to.be(1.5);
  res = hitTest([1, 2.5]);
  expect(res.count).to.be(1);
  expect(res.extent[0]).to.be(2.5);
