it('pixel data reprojected from EPSG:32636 to EPSG:32632 exactly matches original', done => {
  proj4.defs('EPSG:32632', '+proj=utm +zone=32 +datum=WGS84 +units=m +no_defs');
  proj4.defs('EPSG:32636', '+proj=utm +zone=36 +datum=WGS84 +units=m +no_defs');
  register(proj4);
  getProjection('EPSG:32632').setExtent([-3500000, 0, 4500000, 10000000]);
  getProjection('EPSG:32636').setExtent([-3500000, 0, 4500000, 10000000]);
  const extent = [539660, 1835050, 543590, 1838980];
  const tileGrid = createXYZ({
    extent: extent,
    maxZoom: 0
  });
  const source = new DataTileSource({
    loader: loader,
    transition: 0,
    tileGrid: tileGrid,
    projection: 'EPSG:32636'
  });
  const layer = new WebGLTileLayer({
    source: source
  });
  const layerR = new WebGLTileLayer({
    source: source
  });
  map = new Map({
    target: target,
    layers: [layer],
    view: new View({
      projection: 'EPSG:32636'
    })
  });
  mapR = new Map({
    target: targetR,
    layers: [layerR],
    view: new View({
      projection: 'EPSG:32632'
    })
  });
  map.getView().fit(extent);
  map.once('rendercomplete', () => {
    mapR.getView().fit(transformExtent(extent, map.getView().getProjection(), mapR.getView().getProjection()));
    mapR.once('rendercomplete', () => {
      for (let i = 1; i < 255; ++i) {
        let pixel, coordinate, coordinateR, pixelR, dataR, dataA;
        const emptyData = new Uint8Array([0, 0, 0, 0]);
        pixel = [i + 0.5, i + 0.5];
        coordinate = map.getCoordinateFromPixel(pixel);
        coordinateR = transform(coordinate, map.getView().getProjection(), mapR.getView().getProjection());
        pixelR = mapR.getPixelFromCoordinate(coordinateR);
        dataR = layerR.getData(pixelR);
        dataA = [];
        for (let i = -1; i < 2; ++i) {
          for (let j = -1; j < 2; ++j) {
            const data = layer.getData([pixel[0] + i, pixel[1] + j]);
            dataA.push(data.toString());
          }
        }
        expect(dataA).to.contain(dataR.toString());
        pixel = [i + 0.5, 255.5 - i];
        coordinate = map.getCoordinateFromPixel(pixel);
        coordinateR = transform(coordinate, map.getView().getProjection(), mapR.getView().getProjection());
        pixelR = mapR.getPixelFromCoordinate(coordinateR);
        dataR = layerR.getData(pixelR);
        dataA = [];
        for (let i = -1; i < 2; ++i) {
          for (let j = -1; j < 2; ++j) {
            const data = layer.getData([pixel[0] + i, pixel[1] + j]);
            dataA.push(data.toString());
          }
        }
        expect(dataA).to.contain(dataR.toString());
        pixel = [i + 0.5, 1.5];
        coordinate = map.getCoordinateFromPixel(pixel);
        coordinateR = transform(coordinate, map.getView().getProjection(), mapR.getView().getProjection());
        pixelR = mapR.getPixelFromCoordinate(coordinateR);
        dataR = layerR.getData(pixelR);
        dataA = [];
        for (let i = -1; i < 2; ++i) {
          for (let j = -1; j < 2; ++j) {
            const data = layer.getData([pixel[0] + i, pixel[1] + j]);
            dataA.push(data.toString());
          }
        }
        expect(dataA).to.contain(dataR.toString());
        pixel = [1.5, i + 0.5];
        coordinate = map.getCoordinateFromPixel(pixel);
        coordinateR = transform(coordinate, map.getView().getProjection(), mapR.getView().getProjection());
        pixelR = mapR.getPixelFromCoordinate(coordinateR);
        dataR = layerR.getData(pixelR);
        dataA = [];
        for (let i = -1; i < 2; ++i) {
          for (let j = -1; j < 2; ++j) {
            const data = layer.getData([pixel[0] + i, pixel[1] + j]);
            dataA.push(data.toString());
          }
        }
        expect(dataA).to.contain(dataR.toString());
        pixel = [i + 0.5, 255.5];
        coordinate = map.getCoordinateFromPixel(pixel);
        coordinateR = transform(coordinate, map.getView().getProjection(), mapR.getView().getProjection());
        pixelR = mapR.getPixelFromCoordinate(coordinateR);
        dataR = layerR.getData(pixelR);
        dataA = [];
        for (let i = -1; i < 2; ++i) {
          for (let j = -1; j < 2; ++j) {
            const data = layer.getData([pixel[0] + i, pixel[1] + j]);
            dataA.push((data || emptyData).toString());
          }
        }
        expect(dataA).to.contain(dataR.toString());
      }
      done();
    });
  });
});

