it('returns the expected URL', function () {
  const coordinate = [829330.2064098881, 5933916.615134273];
  const projection = source.getProjection();
  const regex = /\/tiles\/h(.*)\.jpeg/;
  let tileUrl;
  tileUrl = source.tileUrlFunction(tileGrid.getTileCoordForCoordAndZ(coordinate, 1), 1, projection);
  expect(tileUrl.match(regex)[1]).to.equal(quadKey([1, 1, 0]));
  tileUrl = source.tileUrlFunction(tileGrid.getTileCoordForCoordAndZ(coordinate, 2), 1, projection);
  expect(tileUrl.match(regex)[1]).to.equal(quadKey([2, 2, 1]));
  tileUrl = source.tileUrlFunction(tileGrid.getTileCoordForCoordAndZ(coordinate, 3), 1, projection);
  expect(tileUrl.match(regex)[1]).to.equal(quadKey([3, 4, 2]));
  tileUrl = source.tileUrlFunction(tileGrid.getTileCoordForCoordAndZ(coordinate, 4), 1, projection);
  expect(tileUrl.match(regex)[1]).to.equal(quadKey([4, 8, 5]));
  tileUrl = source.tileUrlFunction(tileGrid.getTileCoordForCoordAndZ(coordinate, 5), 1, projection);
  expect(tileUrl.match(regex)[1]).to.equal(quadKey([5, 16, 11]));
  tileUrl = source.tileUrlFunction(tileGrid.getTileCoordForCoordAndZ(coordinate, 6), 1, projection);
  expect(tileUrl.match(regex)[1]).to.equal(quadKey([6, 33, 22]));
  const url = new URL(tileUrl);
  expect(url.searchParams.get('dpi')).to.equal('d1');
  expect(url.searchParams.get('device')).to.equal('mobile');
  expect(url.searchParams.get('n')).to.equal('z');
});

