it('correctly converts expressions to GLSL', function () {
  expect(expressionToGlsl(context, ['get', 'myAttr'])).to.eql('a_myAttr');
  expect(expressionToGlsl(context, ['var', 'myVar'])).to.eql(uniformNameForVariable('myVar'));
  expect(expressionToGlsl(context, ['time'])).to.eql('u_time');
  expect(expressionToGlsl(context, ['zoom'])).to.eql('u_zoom');
  expect(expressionToGlsl(context, ['resolution'])).to.eql('u_resolution');
  expect(expressionToGlsl(context, ['+', 1, 2, 3, 4])).to.eql('(1.0 + 2.0 + 3.0 + 4.0)');
  expect(expressionToGlsl(context, ['*', 1, 2, 3, 4])).to.eql('(1.0 * 2.0 * 3.0 * 4.0)');
  expect(expressionToGlsl(context, ['*', [255, 127.5, 0, 0.5], 'red'], ValueTypes.ANY)).to.eql('(vec4(0.5, 0.25, 0.0, 0.5) * vec4(1.0, 0.0, 0.0, 1.0))');
  expect(expressionToGlsl(context, ['+', ['*', ['get', 'size'], 0.001], 12])).to.eql('((a_size * 0.001) + 12.0)');
  expect(expressionToGlsl(context, ['/', ['-', ['get', 'size'], 20], 100])).to.eql('((a_size - 20.0) / 100.0)');
  expect(expressionToGlsl(context, ['clamp', ['get', 'attr2'], ['get', 'attr3'], 20])).to.eql('clamp(a_attr2, a_attr3, 20.0)');
  expect(expressionToGlsl(context, ['^', ['%', ['time'], 10], 2])).to.eql('pow(mod(u_time, 10.0), 2.0)');
  expect(expressionToGlsl(context, ['abs', ['-', ['get', 'attr3'], ['get', 'attr2']]])).to.eql('abs((a_attr3 - a_attr2))');
  expect(expressionToGlsl(context, ['floor', 1])).to.eql('floor(1.0)');
  expect(expressionToGlsl(context, ['round', 1])).to.eql('floor(1.0 + 0.5)');
  expect(expressionToGlsl(context, ['ceil', 1])).to.eql('ceil(1.0)');
  expect(expressionToGlsl(context, ['sin', 1])).to.eql('sin(1.0)');
  expect(expressionToGlsl(context, ['cos', 1])).to.eql('cos(1.0)');
  expect(expressionToGlsl(context, ['atan', 1])).to.eql('atan(1.0)');
  expect(expressionToGlsl(context, ['atan', 1, 0.5])).to.eql('atan(1.0, 0.5)');
  expect(expressionToGlsl(context, ['sqrt', 100])).to.eql('sqrt(100.0)');
  expect(expressionToGlsl(context, ['>', 10, ['get', 'attr4']])).to.eql('(10.0 > a_attr4)');
  expect(expressionToGlsl(context, ['>=', 10, ['get', 'attr4']])).to.eql('(10.0 >= a_attr4)');
  expect(expressionToGlsl(context, ['<', 10, ['get', 'attr4']])).to.eql('(10.0 < a_attr4)');
  expect(expressionToGlsl(context, ['<=', 10, ['get', 'attr4']])).to.eql('(10.0 <= a_attr4)');
  expect(expressionToGlsl(context, ['==', 10, ['get', 'attr4']])).to.eql('(10.0 == a_attr4)');
  expect(expressionToGlsl(context, ['==', 'red', ['get', 'attr5']])).to.eql(`(${stringToGlsl(context, 'red')} == a_attr5)`);
  expect(expressionToGlsl(context, ['!=', 10, ['get', 'attr4']])).to.eql('(10.0 != a_attr4)');
  expect(expressionToGlsl(context, ['all', true, ['get', 'attr6']])).to.eql('(true && a_attr6)');
  expect(expressionToGlsl(context, ['any', true, ['get', 'attr6']])).to.eql('(true || a_attr6)');
  expect(expressionToGlsl(context, ['any', true, ['get', 'attr6'], true])).to.eql('(true || a_attr6 || true)');
  expect(expressionToGlsl(context, ['between', ['get', 'attr4'], -4.0, 5.0])).to.eql('(a_attr4 >= -4.0 && a_attr4 <= 5.0)');
  expect(expressionToGlsl(context, ['!', ['get', 'attr6']])).to.eql('(!a_attr6)');
  expect(expressionToGlsl(context, ['array', ['get', 'attr4'], 1, 2, 3])).to.eql('vec4(a_attr4, 1.0, 2.0, 3.0)');
  expect(expressionToGlsl(context, ['color', ['get', 'attr4'], 1, 2, 0.5])).to.eql('(0.5 * vec4(a_attr4 / 255.0, 1.0 / 255.0, 2.0 / 255.0, 1.0))');
  expect(expressionToGlsl(context, ['band', 1])).to.eql('getBandValue(1.0, 0.0, 0.0)');
  expect(expressionToGlsl(context, ['band', 1, -1, 2])).to.eql('getBandValue(1.0, -1.0, 2.0)');
});

