  }], ['00800000017ff80000000000007ff80000000000007ff8000000000000', 'POINT Z EMPTY', {
    littleEndian: false,
    ewkb: true,
    geometryLayout: 'XYZ'
  }], ['01d1070000000000000000f87f000000000000f87f000000000000f87f', 'POINT M EMPTY', {
    littleEndian: true,
    ewkb: false,
    geometryLayout: 'XYM'
  }], ['00000007d17ff80000000000007ff80000000000007ff8000000000000', 'POINT M EMPTY', {
    littleEndian: false,
    ewkb: false,
    geometryLayout: 'XYM'
  }], ['0101000040000000000000f87f000000000000f87f000000000000f87f', 'POINT M EMPTY', {
    littleEndian: true,
    ewkb: true,
    geometryLayout: 'XYM'
  }], ['00400000017ff80000000000007ff80000000000007ff8000000000000', 'POINT M EMPTY', {
    littleEndian: false,
    ewkb: true,
    geometryLayout: 'XYM'
  }], ['01b90b0000000000000000f87f000000000000f87f000000000000f87f000000000000f87f', 'POINT ZM EMPTY', {
    littleEndian: true,
    ewkb: false,
    geometryLayout: 'XYZM'
  }], ['0000000bb97ff80000000000007ff80000000000007ff80000000000007ff8000000000000', 'POINT ZM EMPTY', {
    littleEndian: false,
    ewkb: false,
    geometryLayout: 'XYZM'
  }], ['01010000c0000000000000f87f000000000000f87f000000000000f87f000000000000f87f', 'POINT ZM EMPTY', {
