it('is not dispatched twice', async () => {
  const source = new Source({
    state: 'loading'
  });
  const layer = new Layer({
    source: source
  });
  let calls = 0;
  function handler(event) {
    calls += 1;
  }
  layer.on('sourceready', handler);
  layer.changed();
  await delay(5);
  source.setState('ready');
  await delay(5);
  source.changed();
  await delay(5);
  expect(calls).to.be(1);
});

it('is not dispatched after source is removed', async () => {
  const source = new Source({
    state: 'loading'
  });
  const layer = new Layer({
    source: source
  });
  let calls = 0;
  function handler(event) {
    calls += 1;
  }
  layer.on('sourceready', handler);
  layer.setSource(null);
  source.setState('ready');
  await delay(5);
  expect(calls).to.be(0);
});

it('is dispatched if source is added later', async () => {
  const layer = new Layer({});
  let calls = 0;
  function handler(event) {
    calls += 1;
  }
  layer.on('sourceready', handler);
  const source = new Source({
    state: 'ready'
  });
  layer.setSource(source);
  await delay(5);
  expect(calls).to.be(1);
});

it('is dispatched if new source is set', async () => {
  const layer = new Layer({
    source: new Source({})
  });
  let calls = 0;
  function handler(event) {
    calls += 1;
  }
  layer.on('sourceready', handler);
  await delay(5);
  expect(calls).to.be(1);
