it('is safe to dispatch events of same type in a once listener', function () {
  let callCount = 0;
  observable.once('change', function () {
    observable.changed();
    observable.changed();
  });
  observable.on('change', function () {
    ++callCount;
  });
  expect(function () {
    observable.changed();
  }).to.not.throwException();
  expect(callCount).to.be(3);
});

