it('query returns full op', async () => {
  const doc1 = {
    _id: 'hello world',
    doc: 'all the things',
    views: 17
  };
  const doc2 = {
    _id: 'sup world',
    doc: 'some of the things',
    views: 10
  };
  const expectedOperation = {
    op: 'PUT',
