it('replicates database of 100 entries and loads it from the disk', async () => {
  const entryCount = 100;
  const entryArr = [];
  let timer;
  for (let i = 0; i < entryCount; i++) entryArr.push(i);
  console.log("Writing to database...");
  await mapSeries(entryArr, i => db1.add('hello' + i));
  console.log("Done");
  return new Promise((resolve, reject) => {
    timer = setInterval(async () => {
      if (db2._oplog.length === entryCount) {
        clearInterval(timer);
        const items = db2.iterator({
          limit: -1
        }).collect();
        assert.equal(items.length, entryCount);
        assert.equal(items[0].payload.value, 'hello0');
        assert.equal(items[items.length - 1].payload.value, 'hello99');
        try {
          // Set write access for both clients
          let options = {
            accessController: {
              write: [orbitdb1.identity.id, orbitdb2.identity.id]
            }
          };

          // Get the previous address to make sure nothing mutates it
          const addr = db1.address.toString();

          // Open the database again (this time from the disk)
          options = Object.assign({}, options, {
            path: dbPath1,
            create: false
          });
          const db3 = await orbitdb1.eventlog(addr, options);
          // Set 'localOnly' flag on and it'll error if the database doesn't exist locally
          options = Object.assign({}, options, {
            path: dbPath2,
            localOnly: true
          });
          const db4 = await orbitdb2.eventlog(addr, options);
          await db3.load();
          await db4.load();

          // Make sure we have all the entries in the databases
          const result1 = db3.iterator({
            limit: -1
          }).collect();
          const result2 = db4.iterator({
            limit: -1
          }).collect();
          assert.equal(result1.length, entryCount);
          assert.equal(result2.length, entryCount);
          await db3.drop();
          await db4.drop();
        } catch (e) {
          reject(e);
        }
        resolve();
      }
    }, 1000);
  });
});

it('starts replicating the database when peers connect', async () => {
  const isLocalhostAddress = addr => addr.toString().includes('127.0.0.1');
  await connectPeers(ipfs1, ipfs2, {
    filter: isLocalhostAddress
  });
  console.log('Peers connected');
  const entryCount = 33;
  const entryArr = [];

  // Create the entries in the first database
  for (let i = 0; i < entryCount; i++) {
    entryArr.push(i);
  }
  await mapSeries(entryArr, i => db1.add('hello' + i));

  // Open the second database
  db2 = await orbitdb2.eventlog(db1.address.toString());
  db4 = await orbitdb2.keyvalue(db3.address.toString());

  // Listen for the 'replicated' events and check that all the entries
  // were replicated to the second database
  return new Promise((resolve, reject) => {
    // Check if db2 was already replicated
    let all = db2.iterator({
      limit: -1
    }).collect().length;
    // Run the test asserts below if replication was done
    let finished = all === entryCount;
    db3.events.on('replicated', (address, hash, entry) => {
      reject(new Error("db3 should not receive the 'replicated' event!"));
    });
    db4.events.on('replicated', (address, hash, entry) => {
      reject(new Error("db4 should not receive the 'replicated' event!"));
    });
    db2.events.on('replicated', (address, length) => {
      // Once db2 has finished replication, make sure it has all elements
      // and process to the asserts below
      all = db2.iterator({
        limit: -1
      }).collect().length;
      finished = all === entryCount;
    });
    try {
      const timer = setInterval(() => {
        if (finished) {
          clearInterval(timer);
          const result1 = db1.iterator({
            limit: -1
          }).collect();
          const result2 = db2.iterator({
            limit: -1
          }).collect();
          assert.equal(result1.length, result2.length);
          assert.deepEqual(result1, result2);
          resolve();
        }
      }, 1000);
    } catch (e) {
      reject(e);
    }
  });
});

