it('checks the file every minute', async function () {
  this.clock.tick(1000); // Yield to the event loop

  await delay(0);
  expect(this.logger.level).to.have.been.calledOnceWithExactly(DEFAULT_LEVEL);
  this.logger.level.reset(); // Trace until 1.5 minutes in the future

  const traceUntil = NOW + 90000;
  this.fs.promises.readFile.resolves(traceUntil.toString());
  this.clock.tick(61000);
  await delay(0);
  expect(this.logger.level).to.have.been.calledOnceWithExactly(TRACE_LEVEL);
  this.logger.level.reset();
  this.clock.tick(60000);
  await delay(0);
  expect(this.logger.level).to.have.been.calledOnceWithExactly(DEFAULT_LEVEL);
});

