it('tags errors thrown from an async function', async function () {
  const delay = promisify(setTimeout);

  async function foo() {
    await delay(10);
    throw new Error('foo error');
  }

  async function bar() {
    try {
      await foo();
    } catch (error) {
      throw OError.tag(error, 'failed to bar', {
        bar: 'baz'
      });
    }
  }

  async function baz() {
    try {
      await bar();
    } catch (error) {
      throw OError.tag(error, 'failed to baz', {
        baz: 'bat'
      });
    }
  }

  try {
    await baz();
    expect.fail('should have thrown');
  } catch (error) {
    expectError(error, {
      name: 'Error',
      klass: Error,
      message: 'Error: foo error',
      firstFrameRx: /at foo/
    });
    expectFullStackWithoutStackFramesToEqual(error, ['Error: foo error', 'TaggedError: failed to bar', 'TaggedError: failed to baz']);
    expect(OError.getFullInfo(error)).to.eql({
      bar: 'baz',
      baz: 'bat'
    });
  }
});

it('tags errors thrown from a promise rejection', async function () {
  function foo() {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        reject(new Error('foo error'));
      }, 10);
    });
  }

  async function bar() {
    try {
      await foo();
    } catch (error) {
      throw OError.tag(error, 'failed to bar', {
        bar: 'baz'
      });
    }
  }

  async function baz() {
    try {
      await bar();
    } catch (error) {
      throw OError.tag(error, 'failed to baz', {
        baz: 'bat'
      });
    }
  }

  try {
    await baz();
    expect.fail('should have thrown');
  } catch (error) {
    expectError(error, {
      name: 'Error',
      klass: Error,
      message: 'Error: foo error',
      firstFrameRx: /_onTimeout/
    });
    expectFullStackWithoutStackFramesToEqual(error, ['Error: foo error', 'TaggedError: failed to bar', 'TaggedError: failed to baz']);
    expect(OError.getFullInfo(error)).to.eql({
      bar: 'baz',
      baz: 'bat'
    });
  }
});

it('tags errors yielded through callbacks', function (done) {
  function foo(cb) {
    setTimeout(() => {
      cb(new Error('foo error'));
    }, 10);
  }

  function bar(cb) {
    foo(err => {
      if (err) {
        return cb(OError.tag(err, 'failed to bar', {
          bar: 'baz'
        }));
      }

      cb();
    });
  }

  function baz(cb) {
    bar(err => {
      if (err) {
        return cb(OError.tag(err, 'failed to baz', {
          baz: 'bat'
        }));
      }

      cb();
    });
  }

  baz(err => {
    if (err) {
      expectError(err, {
        name: 'Error',
        klass: Error,
        message: 'Error: foo error',
        firstFrameRx: /_onTimeout/
      });
      expectFullStackWithoutStackFramesToEqual(err, ['Error: foo error', 'TaggedError: failed to bar', 'TaggedError: failed to baz']);
      expect(OError.getFullInfo(err)).to.eql({
        bar: 'baz',
        baz: 'bat'
      });
      return done();
    }

    expect.fail('should have yielded an error');
  });
});

it('is not included in the stack trace if using capture', function () {
  if (!Error.captureStackTrace) return this.skip();
  const err = new Error('test error');
  OError.tag(err, 'test message');
  const stack = OError.getFullStack(err);
  expect(stack).to.match(/TaggedError: test message\n\s+at/);
  expect(stack).to.not.match(/TaggedError: test message\n\s+at [\w.]*tag/);
});

it('still captures a stack trace, albeit including itself', function () {
  const err = new Error('test error');
  OError.tag(err, 'test message');
  expectFullStackWithoutStackFramesToEqual(err, ['Error: test error', 'TaggedError: test message']);
  const stack = OError.getFullStack(err);
  expect(stack).to.match(/TaggedError: test message\n\s+at [\w.]*tag/);
});

it('should not tag more than that', function () {
  const err = new Error('test error');
  OError.tag(err, 'test message 1');
  OError.tag(err, 'test message 2');
  OError.tag(err, 'test message 3');
  OError.tag(err, 'test message 4');
  OError.tag(err, 'test message 5');
  expectFullStackWithoutStackFramesToEqual(err, ['Error: test error', 'TaggedError: test message 1', 'TaggedError: ... dropped tags', 'TaggedError: test message 4', 'TaggedError: test message 5']);
});

it('should handle deep recursion', async function () {
  async function recursiveAdd(n) {
    try {
      if (n === 0) throw new Error('deep error');
      const result = await recursiveAdd(n - 1);
      return result + 1;
    } catch (err) {
      throw OError.tag(err, `at level ${n}`);
    }
  }

  try {
    await recursiveAdd(10);
  } catch (err) {
    expectFullStackWithoutStackFramesToEqual(err, ['Error: deep error', 'TaggedError: at level 0', 'TaggedError: ... dropped tags', 'TaggedError: at level 9', 'TaggedError: at level 10']);
  }
});

it('should handle a singleton error', function (done) {
  const err = new Error('singleton error');

  function endpoint(callback) {
    helper(err => callback(err && OError.tag(err, 'in endpoint')));
  }

  function helper(callback) {
    libraryFunction(err => callback(err && OError.tag(err, 'in helper')));
  }

  function libraryFunction(callback) {
    callback(err);
  }

  endpoint(() => {
    endpoint(err => {
      expect(err).to.exist;
