it('works on both tags and causes', async function () {
  // Here's the actual error.
  function tryToFoo() {
    try {
      throw Error('foo');
    } catch (error) {
      throw OError.tag(error, 'failed to foo', {
        foo: 1
      });
    }
  } // Inside another function that wraps it.


  function tryToBar() {
    try {
      tryToFoo();
    } catch (error) {
      throw new OError('failed to bar').withCause(error);
    }
  } // And it is in another try.


  try {
    try {
      tryToBar();
      expect.fail('should have thrown');
    } catch (error) {
      throw OError.tag(error, 'failed to bat', {
        bat: 1
      });
    }
  } catch (error) {
    // We catch the wrapping error.
    expectError(error, {
      name: 'OError',
      klass: OError,
      message: 'OError: failed to bar',
      firstFrameRx: /tryToBar/
    }); // But the stack contains all of the errors and tags.

    expectFullStackWithoutStackFramesToEqual(error, ['OError: failed to bar', 'TaggedError: failed to bat', 'caused by:', '    Error: foo', '    TaggedError: failed to foo']); // The info from the wrapped cause should be picked up for logging.

    expect(OError.getFullInfo(error)).to.eql({
      bat: 1,
      foo: 1
    }); // But it should still be recorded.

    expect(OError.getFullInfo(error.cause)).to.eql({
      foo: 1
    });
  }
});

