it('should invalidate when the .pnp.js file changes', async function () {
  let Module = require('module');

  let origPnpVersion = process.versions.pnp; // $FlowFixMe[prop-missing]

  let origModuleResolveFilename = Module._resolveFilename;

  try {
    let b = await testCache({
      entries: ['index.js'],
      inputFS,

      async setup() {
        await inputFS.mkdirp(inputDir);
        await inputFS.ncp(path.join(__dirname, '/integration/pnp-require'), inputDir); // $FlowFixMe[incompatible-type]

        process.versions.pnp = 42; // $FlowFixMe[prop-missing]

        Module.findPnpApi = () => // $FlowFixMe
        require(path.join(inputDir, '.pnp.js'));

        await inputFS.mkdirp(path.join(inputDir, 'pnp/testmodule2'));
        await inputFS.writeFile(path.join(inputDir, 'pnp/testmodule2/index.js'), 'exports.a = 4;');
      },

      async update(b) {
        let output = await run(b.bundleGraph);
        assert.equal(output(), 3);
        let pnp = await inputFS.readFile(path.join(inputDir, '.pnp.js'), 'utf8');
        await inputFS.writeFile(path.join(inputDir, '.pnp.js'), pnp.replace("'pnp', 'testmodule'", "'pnp', 'testmodule2'"));
        delete require.cache[path.join(inputDir, '.pnp.js')];
        await sleep(100);
      }

    }, 'pnp-require');
    let output = await run(b.bundleGraph);
    assert.equal(output(), 6);
  } finally {
    // $FlowFixMe[incompatible-type]
    process.versions.pnp = origPnpVersion; // $FlowFixMe[prop-missing]

    Module._resolveFilename = origModuleResolveFilename;
  }
});

it('should support resolver inside stylus file', async function () {
  let b = await testCache({
    entries: ['index.js'],

    async setup() {
      await overlayFS.writeFile(path.join(inputDir, 'index.styl'), `
      @import "./b";
      .a
        background: red
      `);
      await overlayFS.mkdirp(path.join(inputDir, 'b'));
      await overlayFS.writeFile(path.join(inputDir, 'b/index.styl'), `
      .b
        background: blue
      `);
    },

    async update(b) {
      let css = await overlayFS.readFile(b.bundleGraph.getBundles().find(b => b.type === 'css')?.filePath, 'utf8');
      assert(css.includes('.a {'));
      assert(css.includes('.b {'));
      assert(!css.includes('.c {'));
      await overlayFS.writeFile(path.join(inputDir, 'b.styl'), `
      .c
        background: blue
      `);
    }

  }, 'stylus');
  let css = await overlayFS.readFile(b.bundleGraph.getBundles().find(b => b.type === 'css')?.filePath, 'utf8');
  assert(css.includes('.a {'));
  assert(!css.includes('.b {'));
  assert(css.includes('.c {'));
});

