it('should support yarn pnp', async function () {
  let Module = require('module'); // $FlowFixMe[incompatible-type]


  let origPnpVersion = process.versions.pnp; // $FlowFixMe[prop-missing]

  let origModuleResolveFilename = Module._resolveFilename; // We must create a worker farm that only uses a single thread because our process.versions.pnp
  // mock won't be available in the workers of the existing farm.

  let workerFarm = createWorkerFarm({
    maxConcurrentWorkers: 0
  });

  try {
    let b = await testCache({
      inputFS,
      outputFS: inputFS,
      workerFarm,

      async setup() {
        await inputFS.mkdirp(inputDir);
        await inputFS.ncp(path.join(__dirname, '/integration/cache'), inputDir); // $FlowFixMe

        process.versions.pnp = 42;
        fs.renameSync(path.join(inputDir, 'node_modules'), path.join(inputDir, 'pnp'));
        await inputFS.ncp(path.join(inputDir, 'pnp'), path.join(inputDir, 'pnp2'));
        await inputFS.writeFile(path.join(inputDir, 'pnp', 'parcel-transformer-mock', 'index.js'), `
            const Transformer = require('@parcel/plugin').Transformer;
            module.exports = new Transformer({
              transform({asset}) {
                return [
                  {
                    type: 'js',
                    content: 'TRANSFORMED CODE',
                  },
                ];
              }
            });
            `);
        await inputFS.writeFile(path.join(inputDir, 'pnp2', 'parcel-transformer-mock', 'index.js'), `
            const Transformer = require('@parcel/plugin').Transformer;
            module.exports = new Transformer({
              transform({asset}) {
                return [
                  {
                    type: 'js',
                    content: 'UPDATED',
                  },
                ];
              }
            });
            `);
        await inputFS.writeFile(path.join(inputDir, '.pnp.js'), `
            const path = require('path');
            const resolve = request => {
              if (request === 'parcel-transformer-mock' || request === 'foo') {
                return path.join(__dirname, 'pnp', request);
              } else if (request === 'pnpapi') {
                return __filename;
              } else {
                return require.resolve(request);
              }
            };

            module.exports = {resolveToUnqualified: resolve, resolveRequest: resolve};
            `); // $FlowFixMe[prop-missing]

        Module.findPnpApi = () => // $FlowFixMe
        require(path.join(inputDir, '.pnp.js'));

        await inputFS.writeFile(path.join(inputDir, '.parcelrc'), JSON.stringify({
          extends: '@parcel/config-default',
          transformers: {
            '*.js': ['parcel-transformer-mock']
          }
        }));
      },

      async update(b) {
        let output = await overlayFS.readFile(b.bundleGraph.getBundles()[0].filePath, 'utf8');
        assert(output.includes('TRANSFORMED CODE'));
        await inputFS.writeFile(path.join(inputDir, '.pnp.js'), `
            const path = require('path');
            const resolve = request => {
              if (request === 'parcel-transformer-mock' || request === 'foo') {
                return path.join(__dirname, 'pnp2', request);
              } else if (request === 'pnpapi') {
                return __filename;
              } else {
                return require.resolve(request);
              }
            };

            module.exports = {resolveToUnqualified: resolve, resolveRequest: resolve};
            `);
        delete require.cache[path.join(inputDir, '.pnp.js')];
        await sleep(100);
      }

    });
    let output = await overlayFS.readFile(b.bundleGraph.getBundles()[0].filePath, 'utf8');
    assert(output.includes('UPDATED'));
  } finally {
    process.versions.pnp = origPnpVersion; // $FlowFixMe[prop-missing]

    Module._resolveFilename = origModuleResolveFilename;
    await workerFarm.end();
  }
});

