  }));
  evt = await getNextBuild(b);
  assertBundles(evt.bundleGraph, [{
    name: 'alt.js',
    assets: ['index.js']
  }, {
    name: 'alt.module.js',
    assets: ['index.js']
  }]);
  let contents = await outputFS.readFile(path.join(__dirname, '/monorepo/packages/pkg-a/dist/alt.js'), 'utf8');
  assert(contents.includes('return 2'));
  contents = await outputFS.readFile(path.join(__dirname, '/monorepo/packages/pkg-a/dist/alt.module.js'), 'utf8');
  assert(contents.includes('return 2'));
});

it('should not share bundles between targets', async function () {
