it('should support dir-dependency messages from plugins', async function () {
  let inputDir = path.join(__dirname, '/input', Math.random().toString(36).slice(2));
  await inputFS.mkdirp(inputDir);
  await inputFS.ncp(path.join(__dirname, '/integration/postcss-dir-dependency'), inputDir);
  let b = await bundler(path.join(inputDir, 'index.css'));
  let subscription = await b.watch();
  let buildEvent = await getNextBuild(b);
  assert.equal(buildEvent.type, 'buildSuccess');
  let contents = await outputFS.readFile(buildEvent.bundleGraph.getBundles()[0].filePath, 'utf8');
  assert(contents.includes('background: linear-gradient(green, pink), linear-gradient(red, orange)')); // update

  await inputFS.writeFile(path.join(inputDir, 'backgrounds', 'green.txt'), 'linear-gradient(purple, orange)');
  buildEvent = await getNextBuild(b);
  assert.equal(buildEvent.type, 'buildSuccess');
  contents = await outputFS.readFile(buildEvent.bundleGraph.getBundles()[0].filePath, 'utf8');
  assert(contents.includes('background: linear-gradient(purple, orange), linear-gradient(red, orange)')); // create

  await inputFS.writeFile(path.join(inputDir, 'backgrounds', 'orange.txt'), 'linear-gradient(orange, purple)');
  buildEvent = await getNextBuild(b);
  assert.equal(buildEvent.type, 'buildSuccess');
  contents = await outputFS.readFile(buildEvent.bundleGraph.getBundles()[0].filePath, 'utf8');
  assert(contents.includes('background: linear-gradient(purple, orange), linear-gradient(orange, purple), linear-gradient(red, orange)')); // delete

  await inputFS.unlink(path.join(inputDir, 'backgrounds', 'red.txt'));
  buildEvent = await getNextBuild(b);
  assert.equal(buildEvent.type, 'buildSuccess');
  contents = await outputFS.readFile(buildEvent.bundleGraph.getBundles()[0].filePath, 'utf8');
  assert(contents.includes('background: linear-gradient(purple, orange), linear-gradient(orange, purple)'));
  await subscription.unsubscribe();
});

