    }
  });
  let result = await getNextBuildSuccess(parcel);
  let bundle = nullthrows(result.bundleGraph.getBundles().find(b => b.type === 'js')); // Make sure react-refresh transforms were not applied.

  let assets = [];
  bundle.traverse(node => {
    if (node.type === 'asset') {
      assets.push(node.value);
    } else if (node.type === 'dependency') {
      assert(!node.value.specifier.startsWith('react-refresh/runtime') && !node.value.specifier.startsWith('@parcel/transformer-react-refresh-wrap'));
    }
  });

  for (let asset of assets) {
