  await outputFS.symlink(path.join(rootDir, 'node_modules/.origin/pkg@1.0.0/node_modules/pkg'), path.join(rootDir, 'packages/app/node_modules/pkg'));
  await outputFS.symlink(path.join(rootDir, 'node_modules/.origin/pkg@1.0.0/node_modules/pkg'), path.join(rootDir, 'packages/library/node_modules/pkg'));
  let b = await bundle(path.join(__dirname, '/integration/resolve-symlinked-monorepos/packages/app/index.js'), {
    inputFS: overlayFS,
    outputFS
  });
  let output = await run(b);
  assert.strictEqual(output.default, 2);
});

