    assets: ['index.html']
  }, {
    type: 'css',
    assets: ['other.css']
  }, {
    type: 'png',
    assets: ['logo.png']
  }, {
    type: 'jpeg',
    assets: ['image.jpeg']
  }]);
  let file = await outputFS.readFile(path.join(distDir, 'index.html'), 'utf-8');
  let contentBetweenScriptTag = new RegExp(/<\s*script \s*type="application\/ld\+json"\s*>(.*)<\/\s*script\s*>/gm).exec(file)[1];
