  match(jsonldData.logo.url, /logo\.[a-f0-9]+\.png/);
  match(jsonldData.image[0], /image\.[a-f0-9]+\.jpeg/);
  match(jsonldData.image[1], /image\.[a-f0-9]+\.jpeg/);
});

