it('dependency symbols change', async function () {
  let testDir = path.join(__dirname, '/integration/scope-hoisting/es6/update-used-symbols-dependency-symbols');
  let b = bundler(path.join(testDir, 'index.js'), {
    inputFS: overlayFS,
    outputFS: overlayFS
  });
  await overlayFS.mkdirp(testDir);
  await overlayFS.copyFile(path.join(testDir, 'index.1.js'), path.join(testDir, 'index.js'));
  let subscription = await b.watch();

  try {
    let bundleEvent = await getNextBuild(b);
    assert.strictEqual(bundleEvent.type, 'buildSuccess');
    let output = await run(bundleEvent.bundleGraph);
    assert.deepEqual(output, [123]);
    assertDependencyWasExcluded(bundleEvent.bundleGraph, 'a.js', './c.js');
    await overlayFS.copyFile(path.join(testDir, 'index.2.js'), path.join(testDir, 'index.js'));
    bundleEvent = await getNextBuild(b);
    assert.strictEqual(bundleEvent.type, 'buildSuccess');
    output = await run(bundleEvent.bundleGraph);
    assert.deepEqual(output, [123, 789]);
    assert.deepStrictEqual(new Set(bundleEvent.bundleGraph.getUsedSymbols(findAsset(bundleEvent.bundleGraph, 'c.js'))), new Set(['c']));
    await overlayFS.copyFile(path.join(testDir, 'index.1.js'), path.join(testDir, 'index.js'));
    bundleEvent = await getNextBuild(b);
    assert.strictEqual(bundleEvent.type, 'buildSuccess');
    output = await run(bundleEvent.bundleGraph);
    assert.deepEqual(output, [123]);
    assert(!findAsset(bundleEvent.bundleGraph, 'c.js'));
  } finally {
    await subscription.unsubscribe();
  }
});

