  assertBundles(build.bundleGraph, [{
    name: 'index.js',
    assets: ['bundle-url.js', 'cacheLoader.js', 'css-loader.js', 'index.js', 'js-loader.js']
  }, {
    name: 'index.css',
    assets: ['index.css']
  }, {
    name: /local\.[0-9a-f]{8}\.js/,
    assets: ['local.js']
  }, {
    name: /local\.[0-9a-f]{8}\.css/,
    assets: ['local.css']
  }]);
  dir = await outputFS.readdir(distDir);
  assert.deepEqual(dir.length, 8); // bundles + source maps

  let localCSS = build.bundleGraph.getBundles().find(b => b.type === 'css' && path.basename(b.filePath).startsWith('local'));
  invariant(localCSS);
  assert(data.includes(path.basename(localCSS.filePath)));
  assert(data.includes('css-loader'));
});

it('Should create a basic browser sourcemap', async function () {
  let sourceFilename = path.join(__dirname, '/integration/sourcemap/index.js');
  await bundle(sourceFilename);
  let distDir = path.join(__dirname, '/integration/sourcemap/dist/');
  let filename = path.join(distDir, 'index.js');
  let raw = await outputFS.readFile(filename, 'utf8');
  let mapUrlData = await loadSourceMapUrl(outputFS, filename, raw);

  if (!mapUrlData) {
    throw new Error('Could not load map');
  }

  let map = mapUrlData.map;
  let sourceMap = new SourceMap('/');
  sourceMap.addVLQMap(map);
  let input = await inputFS.readFile(path.join(path.dirname(filename), map.sourceRoot, map.sources[0]), 'utf8');
  let sourcePath = 'index.js';
  let name = raw.match(/function (\$.*\$var\$helloWorld)/)[1];
  checkSourceMapping({
    map: sourceMap,
    source: input,
    generated: raw,
    str: 'function helloWorld',
    generatedStr: 'function ' + name,
    sourcePath
  });
  checkSourceMapping({
    map: sourceMap,
    source: input,
    generated: raw,
    str: 'module.exports = helloWorld;',
    generatedStr: 'module.exports = ' + name + ';',
    sourcePath
  });
  checkSourceMapping({
    map: sourceMap,
    source: input,
    generated: raw,
    str: '"hello world"',
    sourcePath
  });
});

