it('should support bundling SVG', async () => {
  let b = await bundle(path.join(__dirname, '/integration/svg/circle.svg'));
  assertBundles(b, [{
    name: 'circle.svg',
    assets: ['circle.svg']
  }, {
    name: 'other1.html',
