  }, {
    type: 'svg',
    assets: ['square.svg']
  }, {
    name: 'other2.html',
    assets: ['other2.html']
  }, {
    type: 'svg',
    assets: ['path.svg']
  }, {
    type: 'svg',
    assets: ['gradient.svg']
  }, {
    type: 'js',
    assets: ['script.js']
  }, {
