  assert(file.includes(`fill="url('/${path.basename(b.getBundles().find(b => b.name.startsWith('gradient')).filePath)}#myGradient')"`));
  assert(file.includes(`<script xlink:href="/${path.basename(b.getBundles().find(b => b.type === 'js' && b.env.sourceType === 'script').filePath)}"`));
  assert(file.includes(`<script href="/${path.basename(b.getBundles().find(b => b.type === 'js' && b.env.sourceType === 'module').filePath)}"`));
  assert(file.includes(`<?xml-stylesheet href="/${path.basename(b.getBundles().find(b => b.type === 'css').filePath)}"?>`));
});

