  await outputFS.writeFile(path.join(inputDir, '/index.js'), 'module.exports = "hello"', 'utf8');
  let b = bundler(path.join(inputDir, '/index.js'), {
    inputFS: overlayFS
  });
  subscription = await b.watch();
  let buildEvent = await getNextBuild(b);
  let output = await run(buildEvent.bundleGraph);
  assert.equal(output, 'hello');
  await outputFS.writeFile(path.join(inputDir, '/index.js'), 'module.exports = "something else"', 'utf8');
  buildEvent = await getNextBuild(b);
  output = await run(buildEvent.bundleGraph);
  assert.equal(output, 'something else');
});

