it('support nested stage names starting with `$`', () => {
  const body = [{
    lookup: {
      from: 'ACollection',
      let: {
        id: '_id'
      },
      as: 'results',
      pipeline: [{
        $match: {
          $expr: {
            $eq: ['$_id', '$$id']
          }
        }
      }]
    }
  }];
  const expected = [{
    $lookup: {
      from: 'ACollection',
      let: {
        id: '_id'
      },
      as: 'results',
      pipeline: [{
        $match: {
          $expr: {
            $eq: ['$_id', '$$id']
          }
        }
      }]
    }
  }];
  const result = AggregateRouter.getPipeline(body);
  expect(result).toEqual(expected);
});

