    return;
  }
  spyOn(require('../lib/Adapters/Auth/httpsRequest'), 'get').and.callFake(options => {
    if (options === 'https://oauth.vk.com/access_token?client_id=appId&client_secret=appSecret&v=5.123&grant_type=client_credentials' || options === 'https://oauth.vk.com/access_token?client_id=appId&client_secret=appSecret&v=5.124&grant_type=client_credentials') {
      return {
