    someField: {
      type: 'String'
    },
    pointerToUser: {
      type: 'Pointer',
      targetClass: '_User'
    }
  }, {
    find: {
      'role:triggerrole': true,
      [triggeruser.id]: true,
      [triggeruser2.id]: true
    },
    create: {
      'role:triggerrole': true,
      [triggeruser.id]: true,
      [triggeruser2.id]: true
    },
    get: {
      'role:triggerrole': true,
      [triggeruser.id]: true,
      [triggeruser2.id]: true
    },
    update: {
      'role:triggerrole': true,
      [triggeruser.id]: true,
      [triggeruser2.id]: true
    },
    addField: {
      'role:triggerrole': true,
      [triggeruser.id]: true,
      [triggeruser2.id]: true
    },
    delete: {
      'role:triggerrole': true,
      [triggeruser.id]: true,
      [triggeruser2.id]: true
    },
    readUserFields: ['pointerToUser'],
    writeUserFields: ['pointerToUser']
  }, {});
  let called = 0;
