  await triggerobject.save(undefined, {
    sessionToken: triggeruser.getSessionToken()
  });
  expect(called).toBe(1);
  await triggerobject.save(undefined, {
    sessionToken: triggeruser.getSessionToken()
  });
  expect(called).toBe(2);
  await triggerobject.save(undefined, {
    sessionToken: triggeruser2.getSessionToken()
  });
