    const object = req.object;
    object.set('before', 'save');
  });
  Parse.Cloud.define('removeme', () => {
    const testObject = new TestObject();
    return testObject.save().then(testObject => {
      const object = new NoBeforeSaveObject({
        remove: testObject
      });
      return object.save();
    }).then(object => {
      object.unset('remove');
      return object.save();
    });
  });
  Parse.Cloud.define('removeme2', () => {
    const testObject = new TestObject();
    return testObject.save().then(testObject => {
      const object = new BeforeSaveObject({
        remove: testObject
      });
      return object.save();
    }).then(object => {
      object.unset('remove');
      return object.save();
    });
  });
  Parse.Cloud.run('removeme').then(aNoBeforeSaveObj => {
    expect(aNoBeforeSaveObj.get('remove')).toEqual(undefined);
    return Parse.Cloud.run('removeme2');
  }).then(aBeforeSaveObj => {
    expect(aBeforeSaveObj.get('before')).toEqual('save');
    expect(aBeforeSaveObj.get('remove')).toEqual(undefined);
    done();
  }).catch(err => {
    jfail(err);
    done();
  });
});

