it('Logs on invalid config', () => {
  const fields = [{
    field: 'requiredUser',
    value: true,
    error: 'requiredUser is not a supported parameter for Cloud Function validations.'
  }, {
    field: 'requireUser',
    value: [],
    error: 'Invalid type for Cloud Function validation key requireUser. Expected boolean, actual array'
  }, {
    field: 'requireMaster',
    value: [],
    error: 'Invalid type for Cloud Function validation key requireMaster. Expected boolean, actual array'
  }, {
    field: 'validateMasterKey',
    value: [],
    error: 'Invalid type for Cloud Function validation key validateMasterKey. Expected boolean, actual array'
  }, {
    field: 'skipWithMasterKey',
    value: [],
    error: 'Invalid type for Cloud Function validation key skipWithMasterKey. Expected boolean, actual array'
  }, {
    field: 'requireAllUserRoles',
    value: true,
    error: 'Invalid type for Cloud Function validation key requireAllUserRoles. Expected array|function, actual boolean'
  }, {
    field: 'requireAnyUserRoles',
    value: true,
    error: 'Invalid type for Cloud Function validation key requireAnyUserRoles. Expected array|function, actual boolean'
  }, {
    field: 'fields',
    value: true,
    error: 'Invalid type for Cloud Function validation key fields. Expected array|object, actual boolean'
  }, {
    field: 'requireUserKeys',
    value: true,
    error: 'Invalid type for Cloud Function validation key requireUserKeys. Expected array|object, actual boolean'
  }];
  for (const field of fields) {
    try {
      Parse.Cloud.define('myFunction', () => {}, {
        [field.field]: field.value
      });
      fail(`Expected error registering invalid Cloud Function validation ${field.field}.`);
    } catch (e) {
      expect(e).toBe(field.error);
    }
  }
});

